INSERT INTO `website_content_blocks` (`id`, `page_id`, `type`, `identifier`, `content`, `order`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('1', '1', 'template', 'about-page-first-section', '{\"heading1\":\"Welcome to Veritas University\",\"content1\":\"<p>Veritas University Abuja (VUNA) is the Catholic University of Nigeria, founded by the Catholic Bishops Conference of Nigeria to provide high-quality tertiary education according to the tradition of the Catholic Church.<\\/p>\",\"content2\":\"<p>We are committed to forming students who are not only intellectually capable but also morally sound, preparing them to become self-reliant and responsible citizens.<\\/p>\",\"imageUrl\":\"\\/assets\\/images\\/about-hero.jpg\",\"slug\":\"about-page-first-section\"}', '1', '1', '2025-12-21 10:08:20', '2025-12-21 10:08:20', NULL);
INSERT INTO `website_content_blocks` (`id`, `page_id`, `type`, `identifier`, `content`, `order`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('2', '1', 'template', 'about-page-second-section', '{\"heading1\":\"Our Journey So Far\",\"imageUrl1\":\"\\/assets\\/images\\/journey.jpg\",\"content1\":\"<p>Since our establishment in 2007, Veritas University has grown from a small campus to a leading institution in Nigeria.<\\/p>\",\"content2\":\"<p>We have expanded our academic programs to include a wide range of undergraduate and postgraduate courses.<\\/p>\",\"content3\":\"<p>Our permanent site in Bwari, Abuja, offers a serene environment conducive to learning and research.<\\/p>\",\"content4\":\"<p>We continue to partner with international institutions to ensure global standards in education.<\\/p>\",\"content5\":\"<p>Looking ahead, we aim to become a world-class university recognized for research and innovation.<\\/p>\",\"slug\":\"about-page-second-section\"}', '2', '1', '2025-12-21 10:08:20', '2025-12-21 10:08:20', NULL);
INSERT INTO `website_content_blocks` (`id`, `page_id`, `type`, `identifier`, `content`, `order`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('3', '1', 'template', 'about-page-third-section', '{\"heading1\":\"Our Foundation\",\"heading2\":\"Mission\",\"heading3\":\"Vision\",\"heading4\":\"Philosophy\",\"heading5\":\"Core Values\",\"imageUrl1\":\"\\/assets\\/images\\/foundation.jpg\",\"content1\":\"<p>Our foundation is built on the principles of academic excellence and moral integrity.<\\/p>\",\"content2\":\"<p>To provide an integral education that combines academic and professional training with moral and spiritual formation.<\\/p>\",\"content3\":\"<p>To be a dynamic and culturally relevant university, providing high-quality research and learning experiences.<\\/p>\",\"content4\":\"<p>We believe in the education of the whole person, fostering critical thinking and ethical leadership.<\\/p>\",\"content5\":\"<ul><li>Academic Excellence<\\/li><li>Moral Integrity<\\/li><li>Service to Humanity<\\/li><li>Social Justice<\\/li><\\/ul>\",\"slug\":\"about-page-third-section\"}', '3', '1', '2025-12-21 10:08:20', '2025-12-21 10:08:20', NULL);
INSERT INTO `website_content_blocks` (`id`, `page_id`, `type`, `identifier`, `content`, `order`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('4', '1', 'template', 'about-page-fourth-section', '{\"heading1\":\"University Management\",\"imageUrl1\":\"\\/assets\\/images\\/management.jpg\",\"content1\":\"<p>Our management team is led by the Vice-Chancellor, supported by principal officers and deans of faculties.<\\/p>\",\"content2\":\"<p>Together, they work to ensure the smooth administration and strategic growth of the university.<\\/p>\",\"slug\":\"about-page-fourth-section\"}', '4', '1', '2025-12-21 10:08:20', '2025-12-21 10:08:20', NULL);
