INSERT INTO `website_content_blocks` (`id`, `page_id`, `type`, `identifier`, `content`, `order`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('66', '4', 'map', 'home-map', '{\"heading\":\"Our Location\",\"description\":\"Find us on the map.\",\"iframe_url\":\"https:\\/\\/www.google.com\\/maps\\/embed?pb=!1m18!1m12!1m3!1d3937.4746850914603!2d7.415966956417917!3d9.291139060217317!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x104e0bfaa64afab3%3A0xf8088b575d0eec30!2sVeritas%20University%20Abuja!5e0!3m2!1sen!2s!4v1738754748247!5m2!1sen!2s\",\"address\":\"Veritas University Abuja, Area Council, Bwari 901101, Federal Capital Territory\",\"email\":\"info@veritas.edu.ng, ictsupport@veritas.edu.ng\",\"phone\":\"+234 8120212639\",\"phone_link\":\"+234 8120212639\"}', '8', '1', '2025-12-21 11:38:10', '2025-12-26 02:06:06', NULL);
INSERT INTO `website_content_blocks` (`id`, `page_id`, `type`, `identifier`, `content`, `order`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('67', '5', 'template', 'about-page-first-section', '{\"heading1\":\"The Making of Veritas University\",\"content1\":\"Veritas University Abuja (VUA), also known as the Catholic University of Nigeria, was founded by the Catholic Bishops Conference of Nigeria through a resolution given at its March 2002 meeting in Abuja. The initiative was born out of the collective desire of the attending Bishops for a University that would provide high-quality tertiary education according to the tradition of the Catholic Church. The Institution received its provisional operation licence in 2007 from the National Universities Commission and commenced admission of students in October 2008 at its take-off campus in Obehie, Abia State, Nigeria. In 2014, it moved to its permanent site with its campus now located in the Bwari Area Council of the Federal Capital Territory, Abuja. Nigeria.\",\"content2\":\"The University emphasizes moral values, self-reliance, and the development of the students\\u2019 entrepreneurial capabilities for the social and economic benefit of the graduates and the Nigerian society. Our campus is a hub of excellence where curiosity meets innovation. From cutting-edge research initiatives to inclusive extracurricular activities, we prepare our students for the challenges of tomorrow while encouraging them to make a meaningful impact on the world today. With a steadfast commitment to academic rigor, ethical leadership, and societal transformation, Veritas University continues to empower future generations to excel and lead in their respective fields..\",\"imageUrl1\":\"https:\\/\\/res.cloudinary.com\\/dbhjueg2l\\/image\\/upload\\/v1745603546\\/abouthero_ixjrwo.webp\",\"imageUrl1_alt\":null,\"imageUrl1_caption\":null}', '1', '1', '2025-12-21 11:55:38', '2025-12-23 14:36:55', NULL);
INSERT INTO `website_content_blocks` (`id`, `page_id`, `type`, `identifier`, `content`, `order`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('68', '5', 'template', 'about-page-second-section', '{\"heading1\":\"Our Journey\",\"content1\":\"Founded in 2007, Veritas University was established to provide a world-class education in Nigeria with a focus on innovation, ethical leadership, and social impact. Over the years, we have grown into a leading institution, recognized for our commitment to academic excellence and research.\",\"content2\":\"2007: Veritas University is founded.\",\"content3\":\"2010: The first batch of graduates completed their programs, many becoming prominent professionals globally.\",\"content4\":\"2015: Introduction of cutting-edge research initiatives in renewable energy and artificial intelligence.\",\"content5\":\"2023 : Recognized as one of Nigeria\\u2019s top universities, with over 30,000 alumni and 50+ academic programs.\",\"imageUrl1\":\"https:\\/\\/res.cloudinary.com\\/dbhjueg2l\\/image\\/upload\\/v1745492247\\/MTG-0080_a83egc.jpg\",\"imageUrl1_alt\":null,\"imageUrl1_caption\":null}', '2', '1', '2025-12-21 11:55:38', '2025-12-26 04:45:51', NULL);
INSERT INTO `website_content_blocks` (`id`, `page_id`, `type`, `identifier`, `content`, `order`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('69', '5', 'template', 'about-page-third-section', '{\"heading1\":\"Our Foundation of Excellence\",\"content1\":\"Founded on Catholic values, Veritas University is built on truth, learning, and service. Our legacy continues to shape minds and inspire purpose.\",\"heading2\":\"Our Vision\",\"content2\":{\"nodeType\":\"document\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Veritas University seeks to be a dynamic community of staff and students with Christian orientation and inspiration who, motivated by the ideals of hard work, integrity, discipline and creativeness, and dedicated to Catholic principles and attitudes, work together to create knowledge for the advancement of the Nigerian society, and to evolve an academic culture of excellence that will make the University rank among the best in the world.\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Like all catholic Universities the world over which have a strong reputation of academic excellence, and whose graduates distinguish themselves in their various fields of learning, due to insistence on quality input, effective monitoring and committed service on the part of the staff, Veritas University, Abuja is poised to model itself after these great catholic Insitutions with due cognizance of the Nigerian milieu so as to achieve the desired results.\",\"marks\":[],\"data\":[]}]}]},\"heading3\":\"Our Mission\",\"content3\":{\"nodeType\":\"document\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Thus, graduates from the Veritas University, Abuja, after completing their studies, should be able to use the knowledge and skills acquired at the Institution for the upliftment of themselves and the Nigerian society. In totality, the University will produce graduates, irrespective of race, gender, political and religious affiliation, who would possess the following qualities:\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"The mission of Veritas University is to provide its students with an integral and holistic formation that combines academic and professional training with physical, moral, spiritual, social, and cultural formation, together with the formation of Christian religious principles and the social Teachings of the Catholic Church. Through research, the University shall engage in the continuous quest for truth. The creation, preservation and dissemination of knowledge for the good of the society (Ex Corde Ecclesiastes no. 30). Based on Christian inspiration and Christ\'s sacrificial witness, the University shall promote authentic human and cultural development modeled on the person of Christ, and shall champion the cause of justice and uprightness in society, work to empower the weak and the marginalized, and promote dialogue and collaboration in human relationships at various levels and among various cultures and religions (Ex Corde Ecclesiastes nos. 31-37). From inception, the University shall encourage and promote research and scholarship in all fields of knowledge (Ex Corde Ecclesiastes, Gen. Norms Art 2:1&2) as well as carry out teaching and other activities by Catholic ideals, principles, and attitudes, while respecting the freedom of conscience of each person.\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Wide and ordered knowledge in an area of academic discipline\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"The ability to reason logically\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"The ability to communicate clearly in speech and writing with confidence\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Competence in numeric and computer literacy\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Exhibition of such moral values as honesty, humility, truthfulness, love, and high ethics in personal and professional life\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Accepting obligations and responsibilities and having well-developed interpersonal skills and the capacity for teamwork.\",\"marks\":[],\"data\":[]}]}]},\"heading4\":\"Our Philosophy\",\"content4\":\"Our university is founded on the philosophy that all knowledge originates from God and that knowledge is for the service of humanity; consequently, knowledge should be disseminated to individuals without hindrance. Thus, in its bid for excellence, Veritas University, Abuja will promote the highest standards of teaching, research, and community service whilst providing a balanced education for the acquisition of knowledge, practical skill\'s and moral rectitude. The philosophy of the Veritas University, Abuja is inline with the challenge posed by the Second Vatican Council to Institutions of higher Learning \\\"to put forth every effort so that the Christian (human) mind may achieve a public, persistent, universal presence in the whole enterprise of advancing higher culture\\\" (gavissimum education, n.10).\",\"heading5\":\"Our Focus\",\"content5\":{\"nodeType\":{\"title\":null,\"description\":\"Wide and ordered knowledge in an area of academic discipline\"},\"data\":{\"title\":null,\"description\":\"The ability to reason logically\"},\"content\":{\"title\":null,\"description\":\"The ability to communicate clearly in speech and writing with confidence\"},\"1766724040214\":{\"title\":null,\"description\":\"Competence in numeric and computer literacy\"},\"1766724063507\":{\"title\":null,\"description\":\"Exhibition of such moral values as honesty, humility, truthfulness, love, and high ethics in personal and professional life\"},\"1766724076889\":{\"title\":null,\"description\":\"Accepting obligations and responsibilities, and having well-developed interpersonal skills and the capacity for teamwork.\"}},\"content5_description\":\"The University shall be research oriented. This arises from the need for a continuous quest for truth, underscoring the Church\'s belief in the intrinsic value of human knowledge. Emphasis shall be placed on research that grapples with the contemporary human, social, economic, political, cultural, ecological and religious problems of the Nigerian society and the wider human community, and what draws from the rich and diverse cultures of Nigerian peoples in analyzing societal problems paying special attention to their ethical and religious dimensions. In this way, the University shall show itself to be an instrument for the promotion and protection of nature, human dignity, justice, and peace, and economic and political values in Nigeria and globally (Ex Corde Ecclesiastes no. 32)\"}', '3', '1', '2025-12-21 11:55:38', '2025-12-26 05:36:34', NULL);
INSERT INTO `website_content_blocks` (`id`, `page_id`, `type`, `identifier`, `content`, `order`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('70', '5', 'template', 'about-page-fourth-section', '{\"heading1\":\"Guiding Forces at Veritas\",\"content1\":\"Guided by faith and vision, our leaders uphold the mission of Veritas University. Through wisdom, service, and dedication, they shape our path and inspire our community.\",\"content2\":\"At Veritas University, leadership is rooted in service, guided by faith, and driven by a shared vision for excellence. Our pillars of leadership \\u2014 from the Pro Chancellor to academic heads, chaplains, and support staff \\u2014 are united by a deep commitment to nurturing not just intellect, but character. They lead with humility and purpose, ensuring that every decision reflects our Catholic values and dedication to the holistic development of our students. Through their wisdom, dedication, and collaborative spirit, they cultivate a thriving environment where truth is pursued, community is fostered, and future leaders are formed.\",\"imageUrl1\":\"https:\\/\\/res.cloudinary.com\\/dbhjueg2l\\/image\\/upload\\/v1745491824\\/VC_rwpbwj.jpg\",\"buttonText\":\"View Management\",\"buttonLink\":\"\\/university-management\",\"imageUrl1_alt\":null,\"imageUrl1_caption\":null}', '4', '1', '2025-12-21 11:55:38', '2025-12-26 03:56:05', NULL);
INSERT INTO `website_content_blocks` (`id`, `page_id`, `type`, `identifier`, `content`, `order`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('72', '6', 'template', 'admission-jupeb-page', '{\"heading1\":\"JUPEB Admissions\",\"content1\":\"With over 3,000 students, Veritas is home to a vibrant community. You\'ll be taught by expert academics and have access to some of the best teaching facilities in Abuja, Nigeria.\",\"imageUrl1\":\"https:\\/\\/res.cloudinary.com\\/dbhjueg2l\\/image\\/upload\\/v1745491830\\/Vuna-1989.jpg-_pqky9v.jpg\",\"imageUrl2\":\"https:\\/\\/res.cloudinary.com\\/dbhjueg2l\\/image\\/upload\\/v1745491827\\/Vuna-1971.jpg-_k99bco.jpg\",\"heading2\":\"JUPEB REQUIREMENTS\",\"content2\":{\"nodeType\":\"document\",\"data\":[],\"content\":[{\"nodeType\":\"ordered-list\",\"data\":[],\"content\":[{\"nodeType\":\"list-item\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Candidates are expected to possess at least five credit passes in their O\\/level results including (English language and Mathematics at not more than 2 sittings in the SSCE\\/GCE organized by WAEC, NECO, NABTEB and should include the major subjects of study)\",\"marks\":[],\"data\":[]}]}]},{\"nodeType\":\"list-item\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Four (4) Passport photograph, birth certificate and certificate of state of origin\",\"marks\":[],\"data\":[]}]}]},{\"nodeType\":\"list-item\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"No JAMB\\/PUTME is required for admission\",\"marks\":[],\"data\":[]}]}]},{\"nodeType\":\"list-item\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Awaiting result candidate can also apply\",\"marks\":[],\"data\":[]}]}]},{\"nodeType\":\"list-item\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Straight admission without screening\",\"marks\":[],\"data\":[]}]}]}]}]},\"heading3\":\"Start Application\",\"content3\":\"Gone through the admission process? Then click the button to start your application.\",\"link1\":\"https:\\/\\/admission.veritas.edu.ng\\/jupeb\",\"heading4\":\"Fill Out Forms\",\"content4\":\"Fill out the forms by providing the appropriate information required. Please ensure that you review your forms properly before submission.\",\"list\":[\"Rev. Fr. Peter Bakwaph === Chairman Admission Committee === +2348039398830\"],\"content2_list_type\":\"numbered\",\"content2_list_items\":{\"1766751510269\":\"Candidates are expected to possess at least five credit passes in their O\\/level results including (English language and Mathematics at not more than 2 sittings in the SSCE\\/GCE organized by WAEC, NECO, NABTEB and should include the major subjects of study)\",\"1766751514463\":\"Four (4) Passport photograph, birth certificate and certificate of state of origin\",\"1766751527197\":\"No JAMB\\/PUTME is required for admission\",\"1766751538871\":\"Awaiting result candidate can also apply\",\"1766751550118\":\"Straight admission without screening\"},\"heading5\":\"Review Of Submissions\",\"content5\":\"Send scanned copies of form and your credentials and send to jupeb@veritas.edu.ng\",\"heading6\":\"Interviewing Process\",\"content6\":\"You can also submit the application forms at the Veritas University JUPEB Foundation School secretariat.\",\"heading7\":\"Final Decision\",\"content7\":\"Upon completion of the interview process, successful candidates will be contacted and issued admission letters.\",\"heading8\":null,\"content8\":null}', '2', '1', '2025-12-21 11:55:38', '2025-12-26 12:21:59', NULL);
