INSERT INTO `website_content_blocks` (`id`, `page_id`, `type`, `identifier`, `content`, `order`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('109', '6', 'template', 'admission-undergraduate-page', '{\"heading1\":\"Undergraduate study\",\"content1\":\"With over 3,000 students, Veritas is home to a vibrant community. You\'ll be taught by expert academics and have access to some of the best teaching facilities in Abuja, Nigeria.\",\"imageUrl1\":\"https:\\/\\/res.cloudinary.com\\/dbhjueg2l\\/image\\/upload\\/v1745491827\\/Vuna-1971.jpg-_k99bco.jpg\",\"imageUrl2\":\"https:\\/\\/res.cloudinary.com\\/dbhjueg2l\\/image\\/upload\\/v1745491830\\/Vuna-1989.jpg-_pqky9v.jpg\",\"heading2\":\"who can apply for our undergraduate programme\",\"content2\":{\"nodeType\":\"document\",\"data\":[],\"content\":[{\"nodeType\":\"ordered-list\",\"data\":[],\"content\":[{\"nodeType\":\"list-item\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Candidate who made Veritas University their first and second choice.\",\"marks\":[],\"data\":[]}]}]},{\"nodeType\":\"list-item\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Candidate who score minimum of 170 in the last UTME examination.\",\"marks\":[],\"data\":[]}]}]},{\"nodeType\":\"list-item\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Candidates who didn\'t choose Veritas University in the last UTME Exams.\",\"marks\":[],\"data\":[]}]}]},{\"nodeType\":\"list-item\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Transfer candidates from other higher institution.\",\"marks\":[],\"data\":[]}]}]},{\"nodeType\":\"list-item\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Direct entry candidate\",\"marks\":[],\"data\":[]}]}]}]}]},\"heading3\":\"Start Application\",\"content3\":\"Visit our admissions portal to complete your application form online. Upload necessary documents and pay the application fee.\",\"link1\":\"https:\\/\\/admission.veritas.edu.ng\",\"heading4\":\"Tuition & Fees\",\"content4\":\"On the admissions portal, you will be requested to pay an Application fee of Ten thousand Naira only (N10,000.00) for Undergraduate students. For transfer students, in addition to the application fee you will also be required to pay a transfer fee of Three hundred thousand Naira only (N300,000.00). Please contact Rev. Fr. Dr. Peter Bakwaph: 08039398830 (Chairman Admission Committee) for more information.\",\"list\":[\"Rev. Fr. Dr. Peter Bakwaph === 08039398830 === petersd@veritas.edu.ng\",\"Rev. Fr. Dr. Peter Bakwaph === 09303293230 === petersd@veritas.edu.ng\",\"Rev. Fr. Dr. Peter Bakwaph === 09303293230 === petersd@veritas.edu.ng\"],\"heading5\":\"Fill Out Forms\",\"content5\":\"Fill out the forms by providing the appropriate information required. Please ensure that you review your forms properly before submission.\",\"heading6\":\"Review Of Submissions\",\"content6\":\"Your submissions will be reviewed by a panel made up of members of the Central Admissions Committee at Veritas University, to ascertain your eligibility to study with us.\",\"heading7\":\"Interviewing Process\",\"content7\":\"After the review, you will be scheduled for an online or physical Interview with a member of the Central Admissions Committee.\",\"heading8\":\"Final Decision\",\"content8\":\"Upon completion of the interview process, successful candidates will be contacted and issued admission letters.\",\"content2_list_type\":\"numbered\",\"content2_list_items\":[\"Candidate who made Veritas University their first and second choice.\",\"Candidate who score minimum of 170 in the last UTME examination.\",\"Candidates who didn\'t choose Veritas University in the last UTME Exams.\",\"Transfer candidates from other higher institution.\",\"Direct entry candidate\"]}', '1', '1', '2025-12-21 15:12:15', '2025-12-26 12:15:52', NULL);
