INSERT INTO `website_news` (`id`, `heading`, `subheading`, `slug`, `image_url`, `overview`, `content`, `email`, `facebook_url`, `instagram_url`, `twitter_url`, `youtube_url`, `linkedin_url`, `whatsapp_number`, `phone_number`, `address`, `page`, `average_time_to_read`, `author`, `published_at`, `is_active`, `created_at`, `updated_at`) VALUES ('1', 'Veritas University Wins National Debate Championship', 'Our students have done it again!', 'debate-championship-win', 'https://res.cloudinary.com/dbhjueg2l/image/upload/v1745492224/MTG-4553_tuouqt.jpg', '{\"nodeType\":\"document\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Veritas University debate team has emerged victorious at the National Universities Debate Championship held in Lagos.\",\"marks\":[],\"data\":[]}]}]}', '{\"nodeType\":\"document\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"The Veritas University debate team has once again demonstrated academic excellence by winning the National Universities Debate Championship. The team, comprising students from various departments, defeated 20 other universities to clinch the trophy. The Vice Chancellor, Prof. Hyacinth Ichoku, congratulated the team and commended their hard work and dedication.\",\"marks\":[],\"data\":[]}]}]}', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '5 min read', 'Media Team', '2025-12-22 00:00:00', '1', '2025-12-21 15:59:35', '2025-12-25 23:59:44');
INSERT INTO `website_news` (`id`, `heading`, `subheading`, `slug`, `image_url`, `overview`, `content`, `email`, `facebook_url`, `instagram_url`, `twitter_url`, `youtube_url`, `linkedin_url`, `whatsapp_number`, `phone_number`, `address`, `page`, `average_time_to_read`, `author`, `published_at`, `is_active`, `created_at`, `updated_at`) VALUES ('2', 'Admissions Open for 2025/2026 Academic Session', 'Apply now for Undergraduate and Postgraduate programs', 'admissions-open-2025', 'https://res.cloudinary.com/dbhjueg2l/image/upload/v1745491827/Vuna-1971.jpg-_k99bco.jpg', '{\"nodeType\":\"document\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Applications are now invited from suitably qualified candidates for admission into Veritas University for the 2025\\/2026 academic session.\",\"marks\":[],\"data\":[]}]}]}', '{\"nodeType\":\"document\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Veritas University, Abuja, has commenced the sale of application forms for the 2025\\/2026 academic session. Interested candidates can apply for various Undergraduate and Postgraduate programs. The University offers a serene learning environment, modern facilities, and a commitment to moral and academic excellence. Visit the admissions portal to apply.\",\"marks\":[],\"data\":[]}]}]}', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3 min read', 'Admissions Unit', '2025-12-20 00:00:00', '1', '2025-12-21 15:59:35', '2025-12-26 01:36:09');
INSERT INTO `website_news` (`id`, `heading`, `subheading`, `slug`, `image_url`, `overview`, `content`, `email`, `facebook_url`, `instagram_url`, `twitter_url`, `youtube_url`, `linkedin_url`, `whatsapp_number`, `phone_number`, `address`, `page`, `average_time_to_read`, `author`, `published_at`, `is_active`, `created_at`, `updated_at`) VALUES ('3', 'Convocation Ceremony Set for November', 'Celebrating our graduating students', 'convocation-ceremony-2025', 'https://res.cloudinary.com/dbhjueg2l/image/upload/v1745492279/MTG-4591_e2a3hv.jpg', '{\"nodeType\":\"document\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Veritas University is set to hold its 14th Convocation Ceremony this November to celebrate the achievements of its graduating students.\",\"marks\":[],\"data\":[]}]}]}', '{\"nodeType\":\"document\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"The Vice Chancellor has announced that the 14th Convocation Ceremony of Veritas University will take place in November 2025. The event will feature the award of degrees, presentation of prizes, and the investiture of the new Chancellor. Parents, guardians, and the general public are invited to join in the celebration.\",\"marks\":[],\"data\":[]}]}]}', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '4 min read', 'Registry', '2025-12-17 22:38:50', '1', '2025-12-21 15:59:35', '2025-12-22 22:38:51');
INSERT INTO `website_news` (`id`, `heading`, `subheading`, `slug`, `image_url`, `overview`, `content`, `email`, `facebook_url`, `instagram_url`, `twitter_url`, `youtube_url`, `linkedin_url`, `whatsapp_number`, `phone_number`, `address`, `page`, `average_time_to_read`, `author`, `published_at`, `is_active`, `created_at`, `updated_at`) VALUES ('4', 'Veritas Hosts Chimamnda Ngozi Adichie', 'It was a joyous moment for most of the students, many of whom were seeing the writer for the first time in real life, and they took advantage of the moment to make the best of the opportunity of her visit.', 'veritas-hosts-chimamnda-ngozi-adichie', 'https://res.cloudinary.com/dbhjueg2l/image/upload/v1766702934/adichie_bgf51c.jpg', '{\"nodeType\":\"document\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"The leadership of Veritas University, Bwari-Abuja led by the Vice Chancellor, Rev. Fr. Prof. Hyacinth Ichoku led hundred of students and staff of the University to host award winning, renowned Nigerian writer, Chimamanda Ngozi Adichie at the new lecture theatre Wednesday July 2nd, 2025.\",\"marks\":[],\"data\":[]}]}]}', '{\"nodeType\":\"document\",\"data\":[],\"content\":[{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"It was a joyous moment for most of the students, many of whom were seeing the writer for the first time in real life, and they took advantage of the moment to make the best of the opportunity of her visit.\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"The Vice Chancellor of Veritas University, Abuja, Rev. Fr. Professor Hyacinth Ichoku, in a welcome address, said the University was proud to be associated with Ms. Chimamanda Ngozi Adichie.\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"According to the Vice Chancellor,\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"\\u201cIf she were born in an era later than she was, she would have been a product of Veritas University, a citadel of learning that has repeatedly proven that it\'s the place where champions are made.\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"And the records speak for themselves: we are the undisputed national, African and Commonwealth debating champions. We are the proud winners of the African Telecommunications Union Innovation challenge, a continent-wide challenge that came with a princely prize of $20,000!\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Like Chimamanda Adichie that burst on the global stage with her spectacularly successful Purple Hibiscus, neither Veritas nor her students have rested even with those spectacular awards that they have won. One of our students, a young sophomore, is a consultant to ECOWAS, another one was selected as the Microsoft MVP Ambassador; some of our students become chartered accountants before they leave school. Even when they do not remain in their traditional top positions, they do not slip to obscurity. That is the Veritas Spirit.\\u201d\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"In an interactive session moderated by Professor Gabriel Bassey Egbe, the celebrated writer spoke on a wide range of issues ranging from her first book, Purple Hibiscus, to her latest novel, Dreams Count.\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Earlier in the day, the writer was presented with gifts and other items by the University Debating Team.\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Speaking with journalists after her interactive session with the University community, the celebrated writer, who is a known feminist, warned that \\u201cthe world has lost so much by excluding women from participating in various aspects of society including education and politics\\u201d.\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"According to her, feminism was a justice movement like every other movement in the world for groups of people excluded by society. The writer, however, decried that feminism has been politicised because a lot of people do not understand what feminism was truly all about.\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"\\u201cWhen I say feminism is politicized, what I mean is that people don\'t understand what feminism really means. People add all kinds of political meanings. Oh, it\'s a woman who wants to fight men, all of that, but it\\u2019s not.\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"If you think about it, it\'s a justice movement in the same way that racial justice is a justice movement, the movement for people with disabilities, all movements that have excluded groups of people, that\'s the same thing that it is. It\'s a justice movement.\\u201d\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"The writer, who said the world cheated itself thinking it should be all about the men, maintained that the world would have brought in more talent and intelligence to every sector by embracing full inclusion of women.\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"According to her, there was a need to fix certain things where women have been excluded to enable them to participate actively in activities and decision-making. As men and women, though different biologically, they are equal.\",\"marks\":[],\"data\":[]}]},{\"nodeType\":\"paragraph\",\"data\":[],\"content\":[{\"nodeType\":\"text\",\"value\":\"Veritas students had a first-hand opportunity for her to autograph their books while others joined her for group photographs and personal selfies.\",\"marks\":[],\"data\":[]}]}]}', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Veritas PRO', '2025-07-06 00:00:00', '1', '2025-12-25 23:54:23', '2025-12-26 00:21:11');
